import React, { useState } from "react";
import "./App.css";
import Pomodoro from "./pomodoro/Pomodoro";
import Timer from "./pomodoro/Timer";
import useInterval from "./utils/useInterval";

function App() {
  const defaultFocusTime = 25;
  const defaultBreakTime = 5;
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [focusTime, setFocusTime] = useState(defaultFocusTime);
  const [breakTime, setBreakTime] = useState(defaultBreakTime);
  const [sessionTime, setSessionTime] = useState(focusTime * 60);
  const [sessionType, setSessionType] = useState("Focus");
  const [isPaused, setIsPaused] = useState(false);

  const stopTimer = () => {
    setIsTimerRunning((prevState) => !prevState)
    setFocusTime(defaultFocusTime);
    setBreakTime(defaultBreakTime);
    setSessionTime(defaultFocusTime * 60);
    setSessionType("Focus");
  };

  useInterval(() => setSessionTime(sessionTime - 1), isTimerRunning ? 1000 : null);

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }

  if (sessionTime === 0 && sessionType === "Focus") {
    setSessionType("Break");
    setSessionTime(breakTime*60);
  }

  if (sessionTime === 0 && sessionType === "Break") {
    stopTimer()
  }

  return (
    <div className="App">
      <header className="App-header container">
        <h1>Pomodoro Timer</h1>
      </header>
      <div className="container">
        <Pomodoro
          sessionType={sessionType}
          focusTime={focusTime}
          breakTime={breakTime}
          sessionTime={sessionTime}
          isTimerRunning={isTimerRunning}
          setIsTimerRunning={setIsTimerRunning}
          setFocusTime={setFocusTime}
          defaultFocusTime={defaultFocusTime}
          defaultBreakTime={defaultBreakTime}
          setBreakTime={setBreakTime}
          setSessionTime={setSessionTime}
          setSessionType={setSessionType}
          isPaused={isPaused}
          setIsPaused={setIsPaused}
          playPause={playPause}
          stopTimer={stopTimer}
        />
        <Timer
          sessionType={sessionType}
          focusTime={focusTime}
          breakTime={breakTime}
          sessionTime={sessionTime}
          isTimerRunning={isTimerRunning}
          isPaused={isPaused}
          setIsPaused={setIsPaused}
        />
      </div>
    </div>
  );
}

export default App;
