import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import { minutesToDuration } from "../utils/duration";
import { secondsToDuration } from "../utils/duration";



function Pomodoro() {

  const [focusTime, setFocusTime] = useState(defaultFocusTime);

  const [sessionTime, setSessionTime] = useState(focusTime * 60);
  const [sessionType, setSessionType] = useState("Focus");
  let width = 0;

  const stopTimer = () => {
    setSessionType("Focus");
    setSessionTime(focusTime * 60);
    setIsTimerRunning((prevState) => !prevState);
  };

  useInterval(
    () => setSessionTime(sessionTime - 1),
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }

  if (sessionTime === 0 && sessionType === "Focus") {
    setSessionTime(breakTime * 60);
    setSessionType("Break");
  }

  if (sessionTime === 0 && sessionType === "Break") {
    setSessionType("Focus");
    setSessionTime(focusTime * 60);
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              Focus Duration: {minutesToDuration(focusTime)}
            </span>
            <div className="input-group-append">
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                onClick={() => {
                  let newTime = focusTime - 5;
                  setFocusTime(newTime);
                  setSessionTime(newTime * 60);
                }}
                disabled={isTimerRunning || focusTime === 5}
              >
                <span className="oi oi-minus" />
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                onClick={() => {
                  let newTime = focusTime + 5;
                  setFocusTime(newTime);
                  setSessionTime(newTime * 60);
                }}
                disabled={isTimerRunning || focusTime === 60}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                Break Duration: {minutesToDuration(breakTime)}
              </span>
              <div className="input-group-append">
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  onClick={() => {
                    let newTime = breakTime - 1;
                    setBreakTime(newTime);
                    if (sessionType === "Break") setSessionTime(breakTime * 60);
                  }}
                  disabled={isTimerRunning || breakTime === 1}
                >
                  <span className="oi oi-minus" />
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  onClick={() => {
                    let newTime = breakTime + 1;
                    setBreakTime(newTime);
                    if (sessionType === "Break") setSessionTime(breakTime * 60);
                  }}
                  disabled={isTimerRunning || breakTime === 15}
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            <button
              type="button"
              className="btn btn-secondary"
              title="Stop the session"
              onClick={() => stopTimer()}
              disabled={!isTimerRunning}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        {/* TODO: This area should show only when a focus or break session is running or pauses */}
        <div className="row mb-2">
          <div className="col">
            <h2 data-testid="session-title">
              {sessionType === "Focus"
                ? `Focusing for ${minutesToDuration(focusTime)} minutes`
                : `On Break for ${minutesToDuration(breakTime)} minutes`}
            </h2>
            <p className="lead" data-testid="session-sub-title">
              {secondsToDuration(sessionTime)} remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={
                  sessionType === "Focus"
                    ? width =+ 100 - ((sessionTime / (focusTime * 60)) * 100)
                    : width =+ 100 - ((sessionTime / (breakTime * 60)) * 100)
                }
                style={{ width: width }}
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
