import react, {useState} from "react";

export default function Break () {
    const defaultBreakLength = 5;
    const [breakLength, setBreakLength] = useState(defaultBreakLength);
    const [sessionTime, setSessionTime] = useState(defaultFocusTime * 60);

    useInterval(() => setSessionTime(sessionTime - 1), isTimerRunning ? 1000 : null);

    return (
    <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                Break Duration: {minutesToDuration(breakLength)}
              </span>
              <div className="input-group-append">
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  onClick={() => setBreakLength((previousTime) => previousTime - 1)}
                  disabled={isTimerRunning || breakLength === 1}
                >
                  <span className="oi oi-minus" />
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  onClick={() => {
                    setBreakLength((previousTime) => previousTime + 1)
                  }}
                  disabled={isTimerRunning || breakLength === 15}
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
    );
}
