import React, { useState } from "react";
import "./App.css";
import Pomodoro from "./pomodoro/Pomodoro";
import Break from "./pomodoro/Break";
import TimerControl from "./pomodoro/TimerControl"
import useInterval from "./utils/useInterval";
import classNames from "./utils/class-names";
import { minutesToDuration } from "./utils/duration";
import { secondsToDuration } from "./utils/duration";


function App() {
  const defaultFocusTime = 25;
  const defaultBreakTime = 5;
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [breakTime, setBreakTime] = useState(defaultBreakTime);
  const [focusTime, setFocusTime] = useState(defaultFocusTime);
  const [sessionTime, setSessionTime] = useState(focusTime * 60);
  const [sessionType, setSessionType] = useState("Focus");
  let width = 0;

  const incrementBreakLengthByOneMinute = () => {
    let newTime = breakTime + 1;
    setBreakTime(newTime);
    setSessionTime(breakTime * 60);
  };
  const decrementBreakLengthByOneMinute = () => {
    let newTime = breakTime - 1;
    setBreakTime(newTime);
    setSessionTime(breakTime * 60);
  };

  const incrementSessionLengthByFiveMinutes = () => {
    let newTime = focusTime + 5;
    setFocusTime(newTime);
    setSessionTime(newTime * 60);
  };

  const decrementSessionLengthByFiveMinutes = () => {
    let newTime = focusTime - 5;
    setFocusTime(newTime);
    setSessionTime(newTime * 60);
  };

  const stopTimer = () => {
    setSessionType("Focus");
    setSessionTime(focusTime * 60);
    setIsTimerRunning((prevState) => !prevState);
  };

  useInterval(
    () => setSessionTime(sessionTime - 1),
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }

  if (sessionTime === 0 && sessionType === "Focus") {
    setSessionTime(breakTime * 60);
    setSessionType("Break");
  }

  if (sessionTime === 0 && sessionType === "Break") {
    setSessionType("Focus");
    setSessionTime(focusTime * 60);
  }

  return (
    <div className="App">
      <header className="App-header container">
        <h1>Pomodoro Timer</h1>
      </header>
      <div className="container">
        <div className="pomodoro">
          <div className="row">
            <Pomodoro
              isTimerRunning={isTimerRunning}
              focusTime={focusTime}
              minutesToDuration={minutesToDuration}
              incrementSessionLengthByFiveMinutes={
                incrementSessionLengthByFiveMinutes
              }
              decrementSessionLengthByFiveMinutes={
                decrementSessionLengthByFiveMinutes
              }
            />
            <TimerControl isTimerRunning={isTimerRunning} playPause={playPause} classNames={classNames} stopTimer={stopTimer}/>
            <Break

              isTimerRunning={isTimerRunning}
              breakTime={breakTime}
              minutesToDuration={minutesToDuration}
              incrementBreakLengthByOneMinute={incrementBreakLengthByOneMinute}
              decrementBreakLengthByOneMinute={decrementBreakLengthByOneMinute}
            />
            </div>
              {/* TODO: This area should show only when a focus or break session is running or pauses */}
              <div className="row mb-2">
                <div className="col">
                  <h2 data-testid="session-title">
                    {sessionType === "Focus"
                      ? `Focusing for ${minutesToDuration(focusTime)} minutes`
                      : `On Break for ${minutesToDuration(breakTime)} minutes`}
                  </h2>
                  <p className="lead" data-testid="session-sub-title">
                    {secondsToDuration(sessionTime)} remaining
                  </p>
                </div>
              </div>
              <div className="row mb-2">
                <div className="col">
                  <div className="progress" style={{ height: "20px" }}>
                    <div
                      className="progress-bar"
                      role="progressbar"
                      aria-valuemin="0"
                      aria-valuemax="100"
                      aria-valuenow={
                        sessionType === "Focus"
                          ? (width =
                              +100 - (sessionTime / (focusTime * 60)) * 100)
                          : (width =
                              +100 - (sessionTime / (breakTime * 60)) * 100)
                      }
                      style={{ width: width }}
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
  );
}

export default App;
