import React, { useState } from "react";
import "./App.css";
import Pomodoro from "./pomodoro/Pomodoro";
import Controller from "./pomodoro/Controller"

function App() {
  const defaultFocusTime = 25;
  const defaultBreakTime = 5;
  const [focusTime, setFocusTime] = useState(defaultFocusTime);
  const [breakTime, setBreakTime] = useState(defaultBreakTime);
  const [sessionTime, setSessionTime] = useState(focusTime * 60);
  const [sessionType, setSessionType] = useState("Focus");

  return (
    <div className="App">
      <header className="App-header container">
        <h1>Pomodoro Timer</h1>
      </header>
      <div className="container">
        <Pomodoro focusTime={focusTime} breakTime={breakTime} sessionTime={sessionTime} setSessionTime={setSessionTime} sessionType={sessionType}/>
        <Controller focusTime={focusTime} breakTime={breakTime} sessionTime={sessionTime} setSessionTime={setSessionTime} sessionType={sessionType}  />
    </div>
    </div>
  );
};

export default App;
