import React, { useState } from "react";
import "./App.css";
import Pomodoro from "./pomodoro/Pomodoro";
import classNames from "./utils/class-names";
import Active from "./pomodoro/Active";

function App() {
  const defaultFocusTime = 25;
  const defaultBreakTime = 5;

  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [focusTime, setFocusTime] = useState(defaultFocusTime);
  const [breakTime, setBreakTime] = useState(defaultBreakTime);
  const [sessionTime, setSessionTime] = useState(focusTime * 60);
  const [sessionType, setSessionType] = useState("Focus");

  const stopTimer = () => {
    setIsTimerRunning(false);
    setFocusTime(defaultFocusTime);
    setBreakTime(defaultBreakTime);
    setSessionTime(defaultFocusTime * 60);
  };



  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }

  return (
    <div className="App">
      <header className="App-header container">
        <h1>Pomodoro Timer</h1>
      </header>
      <div className="container">
        <Pomodoro isTimerRunning={isTimerRunning} focusTime={focusTime} setFocusTime={setFocusTime} setBreakTime={setBreakTime} breakTime={breakTime} sessionType={sessionType} sessionTime={sessionTime} playPause={playPause} stopTimer={stopTimer}/>
        <Active />
      </div>
    </div>
  );
}

export default App;
