  return (
    <div className="App">
      <header className="App-header container">
        <h1>Pomodoro Timer</h1>
      </header>
      <div className="container">
        <Pomodoro />
        <Progress isTimerRunning={isTimerRunning} isPaused={isPaused}/>
      </div>
    </div>
  );
}

export default App;
