import React, { useState } from "react";
import {minutesToDuration} from '../utils/duration';
import {secondsToDuration} from '../utils/duration';


export default function Active({isTimerRunning, sessionTime, sessionType, focusTime, breakTime}) {
    if (isTimerRunning)
    return (
    <div>
    {/* TODO: This area should show only when a focus or break session is running or pauses */}
    <div className="row mb-2">
      <div className="col">
        {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
        <h2 data-testid="session-title"> {sessionType === "Focus" ? `Focusing for ${minutesToDuration(focusTime)} minutes` : `On Break for ${minutesToDuration(breakTime)} minutes`} </h2>
        {/* TODO: Update message below to include time remaining in the current session */}
        <p className="lead" data-testid="session-sub-title">
        {secondsToDuration(sessionTime)} remaining
        </p>
      </div>
    </div>
    <div className="row mb-2">
      <div className="col">
        <div className="progress" style={{ height: "20px" }}>
          <div
            className="progress-bar"
            role="progressbar"
            aria-valuemin="0"
            aria-valuemax="100"
            aria-valuenow={100-sessionTime/100} // TODO: Increase aria-valuenow as elapsed time increases
            style={{ width: sessionTime/100 }} // TODO: Increase width % as elapsed time increases
          />
        </div>
      </div>
    </div>
  </div>
    )
}
